mapper.select:
select  from t_liquidation_his lh


        AND lh.Fliquidation_id = #{liquidationId}


        AND lh.Forder_id =#{orderId}


        AND lh.Faccount_id = #{accountId}


        AND lh.Faccount_code =#{accountCode}


        AND lh.Faccount_name =#{accountName}


        AND lh.Fterm_code =#{termCode}


        AND lh.Faccount_type =#{accountType}


        AND lh.Forgan_level =#{organLevel}


        AND lh.Forgan_id = #{organId}


        AND lh.Fproduct_id = #{productId}


        AND lh.Ftrans_type =#{transType}


        AND lh.Ftrans_kind =#{transKind}


        AND lh.Fclear_type =#{clearType}


        AND lh.Ffee_type =#{feeType}


        AND lh.Fcash_type =#{cashType}


        AND lh.Fcash_account_id = #{cashAccountId}


        AND lh.Fcard_no =#{cardNo}


        AND lh.Fclear_state =#{clearState}


        AND lh.Fclear_date =#{clearDate}


        AND lh.Fsettle_period =#{settlePeriod}


        AND lh.Fcreate_time = #{createTime}


        AND lh.Forder_time = #{orderTime}


        AND lh.Fclear_time = #{clearTime}


        AND lh.Famount = #{amount}


        AND lh.Forg_amount = #{orgAmount}


        AND lh.Faccount_rate =#{accountRate}


        AND lh.Faccount_fee =#{accountFee}


        AND lh.Forgan2_id = #{organ2Id}


        AND lh.Forgan2_rate =#{organ2Rate}


        AND lh.Forgan2_fee = #{organ2Fee}


        AND lh.Forgan1_id = #{organ1Id}


        AND lh.Forgan1_rate =#{organ1Rate}


        AND lh.Forgan1_fee = #{organ1Fee}


        AND lh.Fgorgan_id = #{gorganId}


        AND lh.Fgorgan_fee = #{gorganFee}


        AND lh.Fchannel_id = #{channelId}


        AND lh.Fchannel_rate =#{channelRate}


        AND lh.Fchannel_cost = #{channelCost}


        AND lh.Fsettle_flag =#{settleFlag}


        AND lh.Fsettle_batchno =#{settleBatchno}


        AND lh.Faffiliate_trans_flag =#{affiliateTransFlag}


        AND lh.Fext_field1 =#{extField1}


        AND lh.Fext_field2 =#{extField2}


        AND lh.Fext_field3 =#{extField3}


        AND lh.Fext_field4 =#{extField4}


        AND lh.Fsplit_column = #{splitColumn}
